import React from 'react'
import style from '../styles/Field.module.css'

class Field extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            field: props.dificult === 'hard' ? [1, 2, 3, 4, 5, 6, 7, 8, 9] :
                    props.dificult === 'medium' ? [1, 2, 3, 4] :
                    props.dificult === 'easy' ? [1, 2] : null
        }
    }

    render() {

        let field = this.state.field

        return (
            <div className={this.props.dificult === 'hard' ? style.field3 : style.field2}>
                {field.map((f, i) => {
                    return(
                        <p key={i} className={style[this.props.dificult]}
                            onClick={() => {
                                this.props.virusPosition === f ?
                                    alert('Acertou')
                                : alert('Errou')
                            }}
                        >
                        </p>
                    )
                })}
            </div>
        )
    }
}

export default Field
