import React from 'react'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false,
            dificult: ''
        }
    }

    verifyHasVirus = (dificult) => {

        let match

        dificult === 'easy' ?
            match = Math.floor(Math.random() * (101 - 90)) + 90 :
        dificult === 'medium' ?
            match = Math.floor(Math.random() * (101 - 70)) + 70 :
        dificult === 'hard' ?
            match = Math.floor(Math.random() * (101 - 50)) + 50 :
        null

        console.log(match, dificult)

        if(match === 100)
            this.setState({hasVirus: true})

    }

    render(){


        return(
            <>
                <div>
                    <input onChange={e => this.setState({dificult: e.target.value})} type='radio' id='easy' value='easy' name='dificult'></input>
                    <label for='easy'>Fácil</label>

                    <input onChange={e => this.setState({dificult: e.target.value})} type='radio' id='medium' value='medium' name='dificult'></input>
                    <label for='medium'>Médio</label>

                    <input onChange={e => this.setState({dificult: e.target.value})} type='radio' id='hard' value='hard' name='dificult'></input>
                    <label for='hard'>Difícil</label>
                </div>
                <button onClick={() => this.verifyHasVirus(this.state.dificult)}>Verificar Vírus</button>
            </>
        )
    }
}

export default App
