import React from 'react'
import Field from '../components/field'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false,
            virusPosition: null,
            canCount: false,
        }
    }

    verifyHasVirus = () => {
        let match = Math.floor(Math.random() * (101 - 90)) + 90
        if( match === 100 ){
            this.setState({
                hasVirus: true,
                virusPosition: Math.floor(Math.random() * (10 - 1)) + 1
            })
        } else {
            this.setState({hasVirus: false})
        }
        console.log(match)
    }

    getVirus = () => {
        this.setState({
            hasVirus: true,
            virusPosition: Math.floor(Math.random() * (10 - 1)) + 1,
            canCount: true
        })
    }


    render(){

        let count = [0, 1, 2, 3, 4, 5]

        this.state.canCount ?
            count.forEach(n => {
                setTimeout(() => {
                    this.setState({
                        virusPosition: Math.floor(Math.random() * (10 - 1)) + 1,
                    })
                }, 1000)

                if(n === 0)
                    this.setState({canCount: false})
            })
        : null

        return(
            <>
                <button onClick={this.getVirus}>Verificar VÃ­rus</button>
            
                {
                    this.state.hasVirus ?
                    <Field hasVirus={this.state.hasVirus} virusPosition={this.state.virusPosition} />
                    : null
                }
            
            </>
        )
    }
}

export default App
