import React from 'react'
import Field from '../components/field'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false,
            virusPosition: null,
            canCount: false,
            dificult: '',
        }
    }

    verifyHasVirus = () => {
        let match = Math.floor(Math.random() * (101 - 90)) + 90
        if( match === 100 ){
            this.setState({
                hasVirus: true,
                virusPosition: Math.floor(Math.random() * (10 - 1)) + 1
            })
        } else {
            this.setState({hasVirus: false})
        }
        console.log(match)
    }

    getVirus = () => {
        this.setState({
            hasVirus: true,
            virusPosition: Math.floor(Math.random() * (10 - 1)) + 1,
            canCount: true
        })
    }


    render(){

        let count = [0, 1, 2, 3, 4, 5]

        this.state.canCount ?
            count.forEach(n => {
                setTimeout(() => {
                    this.setState({
                        virusPosition: Math.floor(Math.random() * (10 - 1)) + 1,
                    })
                    if(n === 0)
                    this.setState({canCount: false})
                }, 1000)
            })
        : null

        return(
            <>
                <div>
                    <input onChange={e => this.setState({dificult: e.target.value})} type='radio' name='dificult' id='easy' value='easy'></input>
                    <label for='easy'>Fácil</label>

                    <input onChange={e => this.setState({dificult: e.target.value})} type='radio' name='dificult' id='medium' value='medium'></input>
                    <label for='medium'>Médio</label>

                    <input onChange={e => this.setState({dificult: e.target.value})} type='radio' name='dificult' id='hard' value='hard'></input>
                    <label for='hard'>Difícil</label>
                </div>
                <button onClick={this.getVirus}>Verificar Vírus</button>
            
                {
                    this.state.hasVirus ?
                    <Field hasVirus={this.state.hasVirus}
                    virusPosition={this.state.virusPosition}
                    dificult={this.state.dificult} />
                    : null
                }
            
            </>
        )
    }
}

export default App
