import React from 'react'
import Field from '../components/field'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false,
            virusPosition: null,
            canCount: false,
            count: 5
        }
    }

    verifyHasVirus = () => {
        let match = Math.floor(Math.random() * (101 - 90)) + 90
        if( match === 100 ){
            this.setState({
                hasVirus: true,
                virusPosition: Math.floor(Math.random() * (10 - 1)) + 1
            })
        } else {
            this.setState({hasVirus: false})
        }
        console.log(match)
    }

    getVirus = () => {
        this.setState({
            hasVirus: true,
            virusPosition: Math.floor(Math.random() * (10 - 1)) + 1,
            canCount: true
        })
    }


    render(){

        this.state.canCount ?
            setInterval(() => {
                this.setState({
                    virusPosition: Math.floor(Math.random() * (10 - 1)) + 1,
                    count: this.state.count - 1
                })
                console.log(this.state.count)
                if(this.state.count === 0){
                    this.setState({
                        canCount: false,
                        count: 5
                    })
                }
            }, 1000)
        : null

        return(
            <>
                <button onClick={this.getVirus}>Verificar VÃ­rus</button>
            
                {
                    this.state.hasVirus ?
                    <Field hasVirus={this.state.hasVirus} virusPosition={this.state.virusPosition} />
                    : null
                }
            
            </>
        )
    }
}

export default App
