import React from 'react'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            count: 0
        }
    }

    render(){

        setInterval(() => {
            let chance = Math.floor(Math.random() * (101 - 90)) + 90
            chance == 100 ?
                alert(chance) : alert('Não foi dessa vez', chance)
        }, 2000);

        return(
            <>
                <h1>Count</h1>
                <h3>{this.state.count}</h3>
            </>
        )
    }
}

export default App
