import React from 'react'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false
        }
    }

    verifyHasVirus = (dificult) => {

        let match

        dificult === 'easy' ?
            match = Math.floor(Math.random() * (101 - 90)) + 90 :
        dificult === 'medium' ?
            match = Math.floor(Math.random() * (101 - 70)) + 90 :
        dificult === 'hard' ?
            match = Math.floor(Math.random() * (101 - 50)) + 90 :
        null

        console.log(match)

        if(match === 100)
            alert('Achou Vírus!')

    }

    render(){


        return(
            <>
                <button>Verificar Vírus</button>
            </>
        )
    }
}

export default App
