import React from 'react'
import style from '../styles/Field.module.css'

class Field extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            field: this.props.dificult === 'hard' ? [1, 2, 3, 4, 5, 6, 7, 8, 9] :
                    this.props.dificult === 'medium' ? [1, 2, 3, 4, 6] :
                    this.props.dificult === 'easy' ? [1, 2, 3] : []
        }
    }

    render() {

        return (
            <div className={style.field}>
                {this.state.field.map((f, i) => {
                    return(
                        <p key={i} className={style.area}
                            onClick={() => {
                                this.props.virusPosition === f ?
                                    alert('Acertou')
                                : alert('Errou')
                            }}
                        >
                        </p>
                    )
                })}
            </div>
        )
    }
}

export default Field
