import style from '../styles/Field.module.css'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false,
            virusPosition: null
        }
    }

    verifyHasVirus = () => {
        let match = Math.floor(Math.random() * (101 - 90)) + 90
        if( match === 100 ){
            this.setState({hasVirus: true})
            this.setState({virusPosition: Math.floor(Math.random() * (10 - 1)) + 1})

