import React from 'react'
import Field from '../components/field'

class App extends React.Component {

    constructor(){
        super()
        this.state = {
            hasVirus: false,
            virusPosition: null
        }
    }

    verifyHasVirus = () => {
        let match = Math.floor(Math.random() * (101 - 90)) + 90
        if( match === 100 ){
            this.setState({hasVirus: true})
            this.setState({virusPosition: Math.floor(Math.random() * (10 - 1)) + 1})

        } else {
            this.setState({hasVirus: false})
        }
        console.log(match)
    }

    getVirus = () => {
        this.setState({hasVirus: true})
        this.setState({virusPosition: Math.floor(Math.random() * (10 - 1)) + 1})
    }


    render(){

        this.state.hasVirus ?
            setInterval(() => {
                this.setState({virusPosition: Math.floor(Math.random() * (10 - 1)) + 1})
            }, 1000)
        : null

        return(
            <>
                <button onClick={this.getVirus}>Verificar VÃ­rus</button>
            
                {
                    this.state.hasVirus ?
                    <Field hasVirus={this.state.hasVirus} virusPosition={this.state.virusPosition} />
                    : null
                }
            
            </>
        )
    }
}

export default App
